import { useState } from "react"


export const useCalculator = () => {
    const [number, set] = useState('');

    const handleChange = (e) =>{
      setNumber(value.concat(e.target.value));
    }

    const handleClear = () => {
      setNumber('');
    }

    const handleResult = () => {
      try {
        setNumber(eval(value));
      } catch (error) {
        setNumber('Error');
      }
    }


  return [ handleChange, handleClear, handleResult, value ];
}
