import { useState } from "react"


export const useCalculator = (initialState = 0) => {
    const [calculator, setCalculator] = useState(initialState);

    const sumNumbers = (a, b) => {
        setCalculator( a + b);
    };

    const restNumbers = (a, b) => {
        setCalculator( a - b);
    };

    const divideNumbers = (a, b) => {
        setCalculator( a / b);
    };

    const multNumbers = (a, b) => {
        setCalculator( a * b);
    };

    const clear = () => {
        setCalculator(initialState);
    };

  return [sumNumbers, restNumbers, divideNumbers, multNumbers, clear, calculator];
}
